<template>
  <div v-if="getShow" class="firstLoginContainer">
    <a-row class="enter-x" justify="space-between">
      <span text-center>For First Time user Please Scan The QR code to bind with your Google Authenticator App</span>
    </a-row>
    <a-row class="enter-x" justify="center">
      <a-col :span="24" style="display: flex; justify-content: center; align-items: center">
        <a-image
          :v-if="!qrCode"
          :width="400"
          :height="400"
          :src="qrCode"
          :class="{ loading: imgLoading }"
          fallback="data:image/png;base64,"
          :preview="{}"
        />
      </a-col>
    </a-row>
    <a-row class="enter-x" justify="space-between">
      <a-col :span="10">
        <a-button block @click="handleBackLogin">
          {{ t('common.return') }}
        </a-button>
      </a-col>
      <a-col :span="10">
        <a-button block @click="handleGotoAuthenticate" type="primary" large>
          {{ t('entry.login') }}
        </a-button>
      </a-col>
    </a-row>
  </div>
</template>

<script setup lang="ts">
import { LoginStateEnum, useLoginState } from '../useLogin'
import { useUserStore } from '~/stores/modules/user'
import { useI18n } from 'vue-i18n'
import { generateQRcode } from '~/supabase/login'
import { Modal } from 'ant-design-vue'
import { createVNode } from 'vue'
import { ExclamationCircleOutlined } from '@ant-design/icons-vue'
import { router } from '~/router'
import { EnumPath } from '~/enums'
const { t } = useI18n()

const user = useUserStore()

const { handleBackLogin, getLoginState, setLoginState } = useLoginState()
const getShow = computed(() => unref(getLoginState) === LoginStateEnum.FIRST_LOGIN)
const qrCode = ref('')
const imgLoading = ref(false)

const { username } = user.getUserInfo

const generateQr = async () => {
  const code = await generateQRcode(username)
  qrCode.value = code.imgLink as string
}

const handleGotoAuthenticate = () => {
  Modal.confirm({
    title: t('entry.continueRegister'),
    icon: createVNode(ExclamationCircleOutlined),
    content: t('entry.registerContinueModalContent'),
    okText: t('common.confirm'),
    cancelText: t('common.return'),
    onOk() {
      setLoginState(LoginStateEnum.ATHENTICATOR)
      // router.push(EnumPath.USERS)
    },
  })
}

watch(getShow, async (isShown) => {
  if (isShown) {
    return generateQr()
  }

  qrCode.value = ''
})
</script>

<style scoped lang="less">
.firstLoginContainer {
  display: flex;
  flex-direction: column;
  gap: 5rem;
}

:deep(.ant-image-img) {
  width: 400px;
  height: 400px;
  &.loading {
    width: 50px;
    height: 50px;
    content: url(./../../../assets/images/spinner.gif);
  }
}

.ant-image-preview-operations-operation:not(:not(:nth-child(n + 4))) {
  display: none;
}
</style>
